import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [page, setPage] = useState("login");
  const [error, setError] = useState("");
  const [user, setUser] = useState(null);

  const handleLogin = async (username, password) => {
    try {
      const res = await axios.post("http://localhost:5001/api/login", { username, password });
      setUser(res.data.user);
      setPage("admin");
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "Server error");
    }
  };

  const handleSignup = async (data) => {
    try {
      await axios.post("http://localhost:5001/api/signup", data);
      setError("");
      alert("User registered successfully.");
      setPage("login");
    } catch (err) {
      setError(err.response?.data?.error || "Server error");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      {page === "login" && <Login onLogin={handleLogin} error={error} setPage={setPage} />}
      {page === "signup" && <Signup onSignup={handleSignup} error={error} setError={setError} setPage={setPage} />}
      {page === "admin" && <AdminPanel user={user} setPage={setPage} />}
      {page === "client" && <ClientPortal setPage={setPage} />}
    </div>
  );
};

const Login = ({ onLogin, error, setPage }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
      <h2 className="text-2xl font-bold text-blue-600 mb-6">Admin Login</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username or Medical ID"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
        />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
          Login
        </button>
      </form>
      <p className="mt-4 text-center">
        New user?{" "}
        <span className="text-blue-600 cursor-pointer" onClick={() => setPage("signup")}>
          Sign up
        </span>
      </p>
    </div>
  );
};

const Signup = ({ onSignup, error, setError, setPage }) => {
  const [data, setData] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    medicalId: "",
    username: "",
    password: "",
    retypePassword: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (data.password !== data.retypePassword) {
      setError("Passwords do not match");
      return;
    }
    onSignup(data);
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
      <h2 className="text-2xl font-bold text-blue-600 mb-6">Admin Signup</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="First Name"
          value={data.firstName}
          onChange={(e) => setData({ ...data, firstName: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="text"
          placeholder="Last Name"
          value={data.lastName}
          onChange={(e) => setData({ ...data, lastName: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={data.phone}
          onChange={(e) => setData({ ...data, phone: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="text"
          placeholder="Medical ID"
          value={data.medicalId}
          onChange={(e) => setData({ ...data, medicalId: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="text"
          placeholder="Username"
          value={data.username}
          onChange={(e) => setData({ ...data, username: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="password"
          placeholder="Password"
          value={data.password}
          onChange={(e) => setData({ ...data, password: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <input
          type="password"
          placeholder="Retype Password"
          value={data.retypePassword}
          onChange={(e) => setData({ ...data, retypePassword: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
          Sign Up
        </button>
      </form>
      <p className="mt-4 text-center">
        Already have an account?{" "}
        <span className="text-blue-600 cursor-pointer" onClick={() => setPage("login")}>
          Log in
        </span>
      </p>
    </div>
  );
};

const AdminPanel = ({ user, setPage }) => {
  const [newRecord, setNewRecord] = useState({
    ticketId: "",
    name: "",
    diagnosis: "",
    date: "",
    details: "",
    key: ""
  });
  const [file, setFile] = useState(null);
  const [patients, setPatients] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5001/api/patients")
      .then(res => setPatients(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleAddRecord = async () => {
    if (!newRecord.ticketId || !newRecord.name || !newRecord.diagnosis || !newRecord.date || !newRecord.details || !newRecord.key) {
      alert("All fields are required");
      return;
    }
    if (newRecord.key.length !== 16) {
      alert("AES key must be 16 characters");
      return;
    }
    const formData = new FormData();
    Object.keys(newRecord).forEach(key => formData.append(key, newRecord[key]));
    if (file) formData.append("file", file);

    try {
      await axios.post("http://localhost:5001/api/patient", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setNewRecord({ ticketId: "", name: "", diagnosis: "", date: "", details: "", key: "" });
      setFile(null);
      const res = await axios.get("http://localhost:5001/api/patients");
      setPatients(res.data);
      alert("Record added successfully");
    } catch (err) {
      alert(err.response?.data?.error || "Server error");
    }
  };

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile && (uploadedFile.type === "application/pdf" || uploadedFile.type === "image/png")) {
      setFile(uploadedFile);
    } else {
      alert("Please upload a PDF or PNG file");
    }
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-4xl">
      <h2 className="text-2xl font-bold text-blue-600 mb-6">Admin Panel</h2>
      <p className="mb-4">Welcome, {user.firstName} {user.lastName}</p>
      <button
        onClick={() => setPage("client")}
        className="mb-4 bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
      >
        Go to Client Portal
      </button>
      <h3 className="text-xl font-semibold mb-4">Add Patient Record</h3>
      <div className="grid grid-cols-2 gap-4">
        <input
          type="text"
          placeholder="Ticket ID (e.g., 1000006)"
          value={newRecord.ticketId}
          onChange={(e) => setNewRecord({ ...newRecord, ticketId: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Patient Name"
          value={newRecord.name}
          onChange={(e) => setNewRecord({ ...newRecord, name: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Diagnosis"
          value={newRecord.diagnosis}
          onChange={(e) => setNewRecord({ ...newRecord, diagnosis: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="date"
          value={newRecord.date}
          onChange={(e) => setNewRecord({ ...newRecord, date: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Details"
          value={newRecord.details}
          onChange={(e) => setNewRecord({ ...newRecord, details: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="text"
          placeholder="AES Key (16 characters)"
          value={newRecord.key}
          onChange={(e) => setNewRecord({ ...newRecord, key: e.target.value })}
          className="p-2 border rounded"
        />
      </div>
      <input
        type="file"
        accept=".pdf,.png"
        onChange={handleFileChange}
        className="mt-4"
      />
      <button
        onClick={handleAddRecord}
        className="mt-4 bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
      >
        Add Record
      </button>
      <h3 className="text-xl font-semibold mt-6 mb-4">Patient Records</h3>
      <table className="w-full border-collapse">
        <thead>
          <tr className="bg-blue-100">
            <th className="border p-2">ID</th>
            <th className="border p-2">Name</th>
            <th className="border p-2">Diagnosis</th>
            <th className="border p-2">Date</th>
            <th className="border p-2">Details</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) => (
            <tr key={patient.ticketId}>
              <td className="border p-2">{patient.ticketId}</td>
              <td className="border p-2">{patient.name}</td>
              <td className="border p-2">{patient.diagnosis}</td>
              <td className="border p-2">{patient.date}</td>
              <td className="border p-2">{patient.details}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const ClientPortal = ({ setPage }) => {
  const [ticketId, setTicketId] = useState("");
  const [aesKey, setAesKey] = useState("");
  const [record, setRecord] = useState(null);
  const [error, setError] = useState("");

  const handleSubmit = async () => {
    try {
      const res = await axios.get(`http://localhost:5001/api/patient/${ticketId}`, { params: { key: aesKey } });
      setRecord(res.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "Server error");
    }
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
      <h2 className="text-2xl font-bold text-blue-600 mb-6">Patient Portal</h2>
      <button
        onClick={() => setPage("login")}
        className="mb-4 bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
      >
        Back to Login
      </button>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {!record ? (
        <div>
          <input
            type="text"
            placeholder="Ticket ID"
            value={ticketId}
            onChange={(e) => setTicketId(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
          />
          <input
            type="text"
            placeholder="AES Key"
            value={aesKey}
            onChange={(e) => setAesKey(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            onClick={handleSubmit}
            className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
          >
            View Record
          </button>
        </div>
      ) : (
        <div>
          <h3 className="text-lg font-semibold mb-4">Patient Record</h3>
          <p><strong>ID:</strong> {record.ticketId}</p>
          <p><strong>Name:</strong> {record.name}</p>
          <p><strong>Diagnosis:</strong> {record.diagnosis}</p>
          <p><strong>Date:</strong> {record.date}</p>
          <p><strong>Details:</strong> {record.details}</p>
          {record.filePath && (
            <p>
              <strong>Report:</strong>{" "}
              <a href={`http://localhost:5001/${record.filePath}`} target="_blank" rel="noopener noreferrer">
                View Report
              </a>
            </p>
          )}
          <button
            onClick={() => setRecord(null)}
            className="mt-4 bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
          >
            Back
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
